import React from 'react';

import './Button.css';

const Button = props => {
  return (
    <button type={props.type} className={`button ${props.enteredValue.trim().length>0 ? 'valid' : ''}`} onClick={props.onClick}>
      {props.children}
    </button>
  );
};

export default Button;
